css:
.pane.with-minimap :not(.minimap-editor):not(.mini) > .editor-contents {
  padding-right: 10%;
}
.pane.with-minimap .vertical-scrollbar {
  right: 10%;
}
.minimap-quick-settings {
  position: absolute !important;
}
.minimap-quick-settings .hidden-input {
  position: absolute;
  width: 0;
  height: 0;
  background: transparent;
  border: none;
}
.minimap-quick-settings ol {
  margin-top: 0 !important;
}
.minimap-quick-settings ol .separator {
  background: rgba(255, 255, 255, 0.07);
  height: 1px;
}
.minimap-quick-settings ol .separator:first-child {
  display: none;
}
.minimap-quick-settings ol li:hover {
  background: rgba(255, 255, 255, 0.07);
}
.open-minimap-quick-settings {
  opacity: 0;
  position: absolute;
  z-index: 1;
  cursor: pointer;
  color: #aaaaaa;
  font-size: 20px;
  text-align: center;
  line-height: 24px;
  width: 24px;
  height: 24px;
  display: block;
  right: 4px;
  transition: opacity 0.4s;
}
.open-minimap-quick-settings:before {
  content: '\f02f';
  font-family: 'Octicons Regular';
}
.minimap:hover .open-minimap-quick-settings {
  opacity: 1;
  transition: opacity 0.1s;
}
.minimap-on-left .pane.with-minimap :not(.minimap-editor):not(.mini) > .editor-contents {
  padding-right: 0;
  padding-left: 10%;
}
.minimap-on-left .pane.with-minimap .vertical-scrollbar {
  right: 0;
}
.minimap-on-left .pane.with-minimap .minimap {
  left: 0;
}
.minimap {
  position: absolute;
  right: 0;
  width: 10%;
  bottom: 0;
  overflow: hidden;
  box-sizing: border-box;
}
.minimap .editor {
  padding: 0 !important;
  background: transparent;
}
.minimap .minimap-scroller {
  position: absolute;
  display: none;
  right: 0;
  width: 2px;
  min-height: 2px;
  z-index: 10;
  background: #4182c4;
}
.minimap .minimap-scroller.visible {
  display: block;
}
.minimap .minimap-wrapper {
  position: absolute;
  width: 100%;
  height: 100%;
  -webkit-transform-origin: 0 0;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
  -webkit-transform: translateZ(0);
  transform: translateZ(0);
}
.minimap .minimap-underlayer,
.minimap .minimap-overlayer {
  -webkit-transform-origin: 0 0;
}
.minimap .minimap-visible-area {
  position: absolute;
  width: 100%;
  background-color: #888;
  opacity: .2;
  cursor: -webkit-grab;
}
.minimap .minimap-visible-area:active {
  cursor: -webkit-grabbing;
}
.minimap .minimap-editor {
  position: absolute;
  cursor: default;
  height: 100%;
  width: 100%;
}
.minimap .minimap-canvas {
  width: 100%;
  height: 100%;
}
.minimap .accelerated {
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
  -webkit-transform: translateZ(0);
  transform: translateZ(0);
}
